mapper.delete:
DELETE
FROM project_info
WHERE id = #{id, jdbcType=VARCHAR}

mapper.update:
UPDATE project_info
SET project_name = #{projectName, jdbcType=VARCHAR},
    project_content = #{projectContent, jdbcType=LONGVARCHAR},
    last_update_date = #{lastUpdateDate, jdbcType=TIMESTAMP}
WHERE id = #{id, jdbcType=VARCHAR}
